    // Start is called before the first frame update
    void Start()
    {
        jogoIniciado = false;
        telaCarregameno.SetActive(true);
        porcentagemCarregamento.text = "0%";
    }

    // Update is called once per frame
    void Update()
    {
        if(vezes < maximo)
        {
            jogoIniciado = false;
            telaCarregameno.SetActive(true);
            porcentagemCarregamento.text = (vezes/(maximo/100)).ToString("F0") + "%";
            SpawnarArvores();
            SpawnarArvores();
            SpawnarArvores();
            SpawnarArvores();
            vezes++;
        }else
        {
            jogoIniciado = true;
            telaCarregameno.SetActive(false);
            porcentagemCarregamento.text = "100%";
        }
    }

